#!/bin/bash
echo "Deleting existing distribution folder"
rm -fr dist
echo "Creating new distribution folder"
mkdir dist
echo "Packaging apidocs"
zip -r dist/apidocs.zip apidocs
echo "Packaging full module"
zip -r dist/full.zip . -x "./node_modules/*" -x "./dist/*"
echo "Packaging minimal module"
zip -r dist/minimal.zip . -x "./node_modules/*" -x "./dist/*" -x "./apidocs/*" -x ".*" -x "./scripts/*" -x "./git-hooks/*" -x "./apidocs.json"
echo "Finished packing"